.icon-text {
  /* white-space: nowrap; */
  display: block;
  overflow: hidden;
  width: 60px !important;
  font-size: 12px;
  font-weight: 300;
  color: black;
  text-overflow: ellipsis;
}

.icon-photos {
  color: black;
  display: flex;
  flex-direction: column;
  margin: 1rem;
  align-items: center;
  text-align: center;
  width: 60px;
  text-overflow: ellipsis;
}

.icon-photos:hover {
  cursor: pointer;
}

.icon-photos:focus .icon-image-photos {
  filter: grayscale(100%) brightness(30%) sepia(100%) hue-rotate(-180deg)
    saturate(400%) contrast(0.9);
}

.icon-photos:focus .icon-text {
  background: rgb(0, 0, 118);
  color: white;
  margin: 0px;
}

.icon-photos:focus .border-box {
  border: rgb(0, 0, 0) dotted 1px;
}

.icon-image-photos {
  width: 30px;
  height: 30px;
  margin-bottom: 5px;
}

.grid-container-photos {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  height: 0 !important;
}

.file-explorer {
  background: white;
  box-shadow: none;
  border-top: solid rgb(0, 0, 0) 1.5px;
  border-left: solid rgb(0, 0, 0) 1.5px;
  border-bottom: solid rgb(250, 250, 250) 1.5px;
  border-right: solid rgb(250, 250, 250) 1.5px;
  height: calc(100% - 54px);
  width: auto;
  z-index: 10;
  margin: 2px;
  padding: 2px 2px 2px 2px;
  overflow-y: auto;
}

/*-------------------------------------------*\
    Windows/Display
\*-------------------------------------------*/

.minimize {
    display: none;
}

.window {
    box-sizing: border-box;
    padding: 0px;
    margin: 0px;
    user-select: none;
    -ms-touch-action: none;
    touch-action: none;
    flex-flow: column;
    display: flex;
}

.fullscreen {
    width: 100% !important;
    height: var(--fullscreen) !important;
    margin: 0;
    transition: all 0.5s ease;
    padding: 0;
}

.content {
    flex-grow: 1;
    overflow-x: hidden;
    padding-right: var(--content-padding-right);
    padding-left: var(--content-padding-left);
    padding-top: var(--content-padding-top);
    padding-bottom: var(--content-padding-bottom);
}

.bottom-bar {
    height: 22px;
    display: flex;
    width: 100%;
}

.bar {
    height: 100%;
    margin: 2px 2px 2px 2px;
    padding: 0px 2px 1px 0px;
    box-shadow: none;
    border-top: solid rgb(0, 0, 0) 1.5px;
    border-left: solid rgb(0, 0, 0) 1.5px;
    border-bottom: solid rgb(250, 250, 250) 1.5px;
    border-right: solid rgb(250, 250, 250) 1.5px;
}

.left-bar {
    width: 100%;
    padding-left: 2px;
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

.right-bar {
    width: 100%;
    padding-left: 2px;
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

/*-------------------------------------------*\
    Top Bar
\*-------------------------------------------*/

.top-bar {
    background: rgb(0, 0, 124);
}

.top-bar-nav {
    display: flex;
    height: 22px;
    width: 100%;
}

.top-bar-text {
    height: 100%;
    padding-left: 12px;
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

.icon-image {
    width: 15px;
    height: 15px;
    margin-right: 5px;
}

.top-bar-window {
    display: flex;
    width: auto;
    align-items: center;
    justify-content: space-between;
    flex-direction: row;
    z-index: 10;
    margin: 2px;
    padding: 2px 2px 2px 2px;
}

.top-bar-deactivated {
    background: rgb(123, 125, 123);
}

.top-bar:hover {
    cursor: default;
}

.window-name {
    color: white;
    display: flex;
    align-items: center;
    font-weight: 500;
    padding: 0;
    font-size: 16px;
    margin: 0 0 0 3px;
}

.icon-image {
    width: 15px;
    height: 15px;
    margin-right: 5px;
    margin-top: 0;
    margin-bottom: 0;
}

